package com.oliveyoung.study.ch02;

public class FloatDoubleExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double var1 = 3.14;
		float var2 = 3.14f;
		float var3 = 3.14F;
	
		System.out.println(var1);
		System.out.println(var2);
		System.out.println(var3);
	}

}
